import React, { useState } from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  Value,
  Handler,
}) {
  const ButtonComponent = ({ target }) => {
    switch (true) {
      case dataTestid === "increase-focus": {
        const preValue = Value.value;
        if (Value.value <= Value.max && Value.value >= Value.min) {
          Value.value += Value.inc;
          console.log(
            `Sanity Check: Button | Value ${preValue} increased to ${Value.value}`
          );
        } else {
          console.log(
            `Sanity Check: Button | Value ${preValue} has hit the Limit`
          );
        }
        break;
      }
      case dataTestid === "decrease-focus": {
        console.log("Sanity Check: Button | Value ", Value);
        break;
      }
      case dataTestid === "increase-break": {
        console.log("Sanity Check: Button | Value ", Value);
        break;
      }
      case dataTestid === "decrease-break": {
        console.log("Sanity Check: Button | Value ", Value);
        break;
      }
      default:
        break;
    }
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={ButtonComponent}
    >
      <span className={className2} />
    </button>
  );
}
