import React, { useState } from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  breakValue,
  breakHandler,
  focusValue,
  focusHandler,
}) {
  function valueIncrement(durationObj) {
    if (durationObj.value < durationObj.max)
      durationObj.value += durationObj.inc;
    return durationObj;
  }

  function valueDecrement(durationObj) {
    if (durationObj.value > durationObj.min)
      durationObj.value -= durationObj.inc;
    return durationObj;
  }

  const buttonCheck = () => {
    switch (true) {
      case dataTestid === "increase-focus": {
        focusHandler(valueIncrement({ ...focusValue }));
        break;
      }
      case dataTestid === "decrease-focus": {
        focusHandler(valueDecrement({ ...focusValue }));
        break;
      }
      case dataTestid === "increase-break": {
        breakHandler(valueIncrement({ ...breakValue }));
        break;
      }
      case dataTestid === "decrease-break": {
        breakHandler(valueDecrement({ ...breakValue }));
        break;
      }
      default: console.log("Something is really wrong!!!");
        break;
    }
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonCheck}
    >
      <span className={className2} />
    </button>
  );
}
