import React, { useState } from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  breakValue,
  breakHandler,
  focusValue,
  focusHandler,
}) {
  function valueIncrement(durationObj) {
    const preValue = durationObj.value;
    if (durationObj.value < durationObj.max) {
      durationObj.value += durationObj.inc;
      console.log(
        `Sanity Check: Button | Value ${preValue} increased to ${durationObj.value}`
      );
    } else {
      console.log(`Sanity Check: Button | Value ${preValue} has hit the Limit`);
    };
  };

  function valueDecrement(durationObj) {
    const preValue = durationObj.value;
    if (durationObj.value > durationObj.min) {
      durationObj.value -= durationObj.inc;
      console.log(
        `Sanity Check: Button | Value ${preValue} increased to ${durationObj.value}`
      );
    } else {
      console.log(`Sanity Check: Button | Value ${preValue} has hit the Limit`);
    };
  };

  const buttonCheck = () => {
    switch (true) {
      case dataTestid === "increase-focus": {
        valueIncrement(focusValue);
        focusHandler(focusValue);
        break;
      };
      case dataTestid === "decrease-focus": {
        valueDecrement(focusValue);
        focusHandler(focusValue);
        break;
      };
      case dataTestid === "increase-break": {
        valueIncrement(breakValue);
        breakHandler(breakValue);
        break;
      };
      case dataTestid === "decrease-break": {
        valueDecrement(breakValue);
        breakHandler(breakValue);
        break;
      };
      default:
        break;
    };
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonCheck}
    >
      <span className={className2} />
    </button>
  );
}
