import React from "react";

export default function Interval({ masterObject, masterHandler }) {
  const Interval = () => {
    if (masterObject.focusCount === 0) {
      masterHandler({
        ...masterObject,
        focusCount: masterObject.focusCur * 60,
        mode: "Break",
      });
    } else if (masterObject.breakCount === 0) {
      masterHandler({
        ...masterObject,
        breakCount: masterObject.breakCur * 60,
        mode: "Focus",
      });
    } else {
      switch (true) {
        case masterObject.mode === "Focus": {
          masterHandler({
            ...masterObject,
            focusCount: masterObject.focusCount - 1,
            state: "Running",
          });
          break;
        }
        case masterObject.mode === "Break": {
          masterHandler({
            ...masterObject,
            breakCount: masterObject.breakCount - 1,
            state: "Running",
          });
          break;
        }
        default:
          break;
      }
    }
  };
  return Interval;
}
