import React from "react";

export default function StopButton({clockValue, clockHandler, focusValue, breakValue, playPause}) {
  const stopButtonPressed = () => {
    clockHandler({
      ...clockValue,
      valFocus: (focusValue.val * 60),
      curFocus: (focusValue.val * 60),
      valBreak: (breakValue.val * 60),
      curBreak: (breakValue.val * 60),
      mode: "Focus",
      state: "Stopped",
    });
    playPause();
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      title="Stop the session"
      onClick={stopButtonPressed}
    >
      <span className="oi oi-media-stop" />
    </button>
  );
}
