import React from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration";

export default function ButtonComponent({
  clockValue,
  clockHandler,
  breakValue,
  focusValue,
}) {
  clockHandler({
    valFocus: focusValue.val,
    curFocus: clockValue.curFocus,
    valBreak: breakValue.val,
    curBreak: clockValue.curBreak,
    mode: clockValue.mode,
  });

  let temp = "";
  let timerTotal = "";
  if (clockValue.mode === "Focus") {
    timerTotal = `Focusing for ${minutesToDuration(
      clockValue.valFocus
    )} minutes`;
    temp = clockValue.curFocus * 60;
  } else if (clockValue.mode === "On Break") {
    timerTotal = `On Break for ${minutesToDuration(
      clockValue.valBreak
    )} minutes`;
    temp = clockValue.curBreak * 60;
  }

  const timerRemaining = `${secondsToDuration(temp)} remaining`;

  return (
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
        <h2 data-testid="session-title">{timerTotal}</h2>
        {/* TODO: Update message below to include time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
          {timerRemaining}
        </p>
      </div>
    </div>
  );
}
