import React from "react";
import {minutesToDuration, secondsToDuration} from '../utils/duration';

export default function ButtonComponent({ breakValue, focusValue }) {
  const timerTotal = `Focusing for ${minutesToDuration(focusValue.val)} minutes`;
  const temp = (focusValue.val * 60) - 1;
  const timerRemaining = `${secondsToDuration(temp)} remaining`;

  return (
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
        <h2 data-testid="session-title">{timerTotal}</h2>
        {/* TODO: Update message below to include time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
          {timerRemaining}
        </p>
      </div>
    </div>
  );
}
