import React from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  breakValue,
  breakHandler,
  focusValue,
  focusHandler,
  clockValue,
  clockHandler,
}) {
  function valueIncrement(durationObj) {
    if (durationObj.val < durationObj.max) durationObj.val += durationObj.inc;
    return durationObj;
  }

  function valueDecrement(durationObj) {
    if (durationObj.val > durationObj.min) durationObj.val -= durationObj.inc;
    return durationObj;
  }

  function clockUpdater(clockObj) {

  }

  const buttonCheck = () => {
    let tempFocus = focusValue;
    let tempBreak = breakValue;
    switch (true) {
      case dataTestid === "increase-focus": {
        tempFocus = valueIncrement({ ...focusValue })
        focusHandler(tempFocus);
        break;
      }
      case dataTestid === "decrease-focus": {
        tempFocus = valueDecrement({ ...focusValue })
        focusHandler(tempFocus);
        break;
      }
      case dataTestid === "increase-break": {
        tempBreak = valueIncrement({ ...breakValue })
        breakHandler(tempBreak);
        break;
      }
      case dataTestid === "decrease-break": {
        tempBreak = valueDecrement({ ...breakValue })
        breakHandler(tempBreak);
        break;
      }
      default:
        console.log("Something is *really* wrong!!!");
        break;
    }
    clockHandler({
      valFocus: tempFocus.val,
      curFocus: clockValue.val,
      valBreak: tempBreak.val,
      curBreak: clockValue.val,
      mode: clockValue.mode,
    })
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonCheck}
    >
      <span className={className2} />
    </button>
  );
}
