import React, { useState } from "react";

export default function ValueModifier({}) {
  const preValue = durationObj.value;
  if (durationObj.value < durationObj.max) {
    durationObj.value += durationObj.inc;
    console.log(
      `Sanity Check: Button | Value ${preValue} increased to ${durationObj.value}`
    );
  } else {
    console.log(`Sanity Check: Button | Value ${preValue} has hit the Limit`);
  }
  return durationObj;
}
