import React from "react";

export default function StopButton({ masterObject, masterHandler, playPause }) {
  const stopButtonPressed = () => {
    if (clockValue.state !== "Stopped") {
      clockHandler({
        valFocus: focusValue.val * 60,
        curFocus: focusValue.val * 60,
        valBreak: breakValue.val * 60,
        curBreak: breakValue.val * 60,
        mode: "Focus",
        state: "Stopped",
      });
      playPause();
    }
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      title="Stop the session"
      onClick={stopButtonPressed}
    >
      <span className="oi oi-media-stop" />
    </button>
  );
}
