import React from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  breakValue,
  breakHandler,
  focusValue,
  focusHandler,
  clockValue,
  clockHandler,
}) {
  function valueIncrement(durationObj) {
    if (durationObj.val < durationObj.max) durationObj.val += durationObj.inc;
    return durationObj;
  }

  function valueDecrement(durationObj) {
    if (durationObj.val > durationObj.min) durationObj.val -= durationObj.inc;
    return durationObj;
  }

  const buttonCheck = () => {
    switch (true) {
      case dataTestid === "increase-focus": {
        const temp = valueIncrement({ ...focusValue })
        focusHandler(temp);
        break;
      }
      case dataTestid === "decrease-focus": {
        const temp = valueDecrement({ ...focusValue })
        focusHandler(temp);
        break;
      }
      case dataTestid === "increase-break": {
        breakHandler(valueIncrement({ ...breakValue }));
        break;
      }
      case dataTestid === "decrease-break": {
        breakHandler(valueDecrement({ ...breakValue }));
        break;
      }
      default:
        console.log("Something is *really* wrong!!!");
        break;
    }
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonCheck}
    >
      <span className={className2} />
    </button>
  );
}
