import React from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  breakValue,
  breakHandler,
  focusValue,
  focusHandler,
  clockValue,
  clockHandler,
}) {
  function valueIncrement(focusValue) {
    if (focusValue.val < focusValue.max) focusValue.val += focusValue.inc;
    return focusValue;
  }

  function valueDecrement(focusValue) {
    if (focusValue.val > focusValue.min) focusValue.val -= focusValue.inc;
    return focusValue;
  }

  const buttonCheck = () => {
    switch (true) {
      case dataTestid === "increase-focus": {
        const temp = valueIncrement({ ...focusValue });
        focusHandler(temp);
        clockHandler({
          ...clockValue,
          valFocus: temp.val * 60,
          curFocus: temp.val * 60,
        });
        break;
      }
      case dataTestid === "decrease-focus": {
        const temp = valueDecrement({ ...focusValue });
        focusHandler(temp);
        clockHandler({
          ...clockValue,
          valFocus: temp.val * 60,
          curFocus: temp.val * 60,
        });
        break;
      }
      case dataTestid === "increase-break": {
        const temp = valueIncrement({ ...breakValue });
        breakHandler(temp);
        clockHandler({
          ...clockValue,
          valBreak: temp.val * 60,
          curBreak: temp.val * 60,
        });
        break;
      }
      case dataTestid === "decrease-break": {
        const temp = valueDecrement({ ...breakValue });
        breakHandler(temp);
        clockHandler({
          ...clockValue,
          valBreak: temp.val * 60,
          curBreak: temp.val * 60,
        });
        break;
      }
      default:
        console.log("Something is *really* wrong!!!");
        break;
    }
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonCheck}
    >
      <span className={className2} />
    </button>
  );
}
