import React from "react";

export default function StopButton({
  masterObject,
  masterHandler,
  isTimerRunning,
  playPause,
  masterDefault,
}) {
  const stopButtonPressed = () => {
    if (masterObject.state !== "Stopped") {
      if (isTimerRunning) playPause();
      masterHandler({
        ...masterDefault,
        focusCur: masterObject.focusCur,
        breakCur: masterObject.breakCur,
        focusCount: masterObject.focusCur * 60,
        breakCount: masterObject.breakCur * 60,
        mode: "Focus",
        state: "Stopped",
        disabled: false,
      });
    };
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      title="Stop the session"
      onClick={stopButtonPressed}
    >
      <span className="oi oi-media-stop" />
    </button>
  );
}
