import React from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration";

export default function Timer({ masterObject, masterHandler }) {
  function topTimer() {
    let temp = "";
    let timerTotal = "";
    if (masterObject.mode === "Focus") {
      timerTotal = `Focusing for ${minutesToDuration(
        (masterObject.focusCount + 1)/60
      )} minutes`;
      temp = masterObject.focusCount;
    } else if (masterObject.mode === "Break") {
      timerTotal = `On Break for ${minutesToDuration(
        (masterObject.breakCount + 1)/60
      )} minutes`;
      temp = masterObject.breakCount;
    }

    const timerRemaining = `${secondsToDuration(temp)} remaining`;
    return (
      <div className="row mb-2">
        <div className="col">
          {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
          <h2 data-testid="session-title">{timerTotal}</h2>
          {/* TODO: Update message below to include time remaining in the current session */}
          <p className="lead" data-testid="session-sub-title">
            {timerRemaining}
          </p>
        </div>
      </div>
    );
  }

  function bottomTimer() {
    return (
      <div className="row mb-2">
        <div className="col">
          <div className="progress" style={{ height: "20px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              aria-valuemin="0"
              aria-valuemax="100"
              aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
              style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
            />
          </div>
        </div>
      </div>
    );
  }

  if (masterObject.state !== "Stopped") {
    return (
      <>
        {topTimer()}
        {bottomTimer()}
      </>
    );
  } else return null;
}
