import React from "react";

export default function ClockEvaluator({
  breakValue,
  focusValue,
  clockValue,
  clockHandler,
  playPause,
}) {
  if (clockValue.curFocus !== 0) {
    switch (true) {
      case clockValue.mode === "Stopped": {
        clockHandler({
          valFocus: focusValue.val * 60,
          curFocus: focusValue.val * 60,
          valBreak: breakValue.val * 60,
          curBreak: breakValue.val * 60,
          mode: "Focus",
          state: "Stopped",
        });
        playPause();
        break;
      }
      case clockValue.mode === "Focus": {
        clockHandler({
          ...clockValue,
          curFocus: clockValue.curFocus - 1,
          state: "Running",
        });
        break;
      }
      case clockValue.mode === "Break": {
        clockHandler({
          ...clockValue,
          curFocus: clockValue.curFocus - 1,
          state: "Running",
        });
        break;
      }
      default: {
        clockHandler({
          valFocus: focusValue.val * 60,
          curFocus: focusValue.val * 60,
          valBreak: breakValue.val * 60,
          curBreak: breakValue.val * 60,
          mode: "Focus",
          state: "Stopped",
        });
        playPause();
        break;
      }
    }
  }
}
