import React, { useState } from "react";

export default function ButtonComponent({
  type,
  className,
  dataTestid,
  className2,
  BreakValue,
  breakHandler,
  FocusValue,
  focusHandler,
}) {
  function valueIncrement(Value) {
    const preValue = Value.value;
    if (Value.value < Value.max) {
      Value.value += Value.inc;
      console.log(
        `Sanity Check: Button | Value ${preValue} increased to ${Value.value}`
      );
    } else {
      console.log(`Sanity Check: Button | Value ${preValue} has hit the Limit`);
    };
  };

  function valueDecrement(Value) {
    const preValue = Value.value;
    if (Value.value > Value.min) {
      Value.value -= Value.inc;
      console.log(
        `Sanity Check: Button | Value ${preValue} increased to ${Value.value}`
      );
    } else {
      console.log(`Sanity Check: Button | Value ${preValue} has hit the Limit`);
    };
  };

  const buttonComponent = ({ target }) => {
    switch (true) {
      case dataTestid === "increase-focus": {
        valueIncrement(FocusValue);
        focusHandler(FocusValue);
        break;
      };
      case dataTestid === "decrease-focus": {
        valueDecrement(FocusValue);
        focusHandler(FocusValue);
        break;
      };
      case dataTestid === "increase-break": {
        valueIncrement(BreakValue);
        breakHandler(BreakValue);
        break;
      };
      case dataTestid === "decrease-break": {
        valueDecrement(BreakValue);
        breakHandler(BreakValue);
        break;
      };
      default:
        break;
    };
  };

  return (
    <button
      type={type}
      className={className}
      data-testid={dataTestid}
      onClick={buttonComponent}
    >
      <span className={className2} />
    </button>
  );
}
